import React, { Component } from 'react';

class InfoModule extends Component {

	constructor(modules){
		super();
	}

	renderModules(){
		let modules = "";
		for (const id in this.props) {
			modules += this.renderModule(id);
		}
		return modules;
	}

	renderModule(id){
		switch(id){
			case "stats":
				return "<Stats />";
			case "deadlines":
				return "<Deadlines />";
			default:
				return "Default";
		}
	}

	render(){
		return(
		<div className="InfoModules">
			{this.renderModules()}
		</div>);
	}
}

export default InfoModule;
