import React, { Component } from 'react';
import "../css/InfoBoard.css";
import InfoModule from "../components/InfoModule";

class InfoBoard extends Component {

	constructor(){
		super();
		this.state = {
			display: 0
		}
	}

	getBoard(id){
		switch(this.state.display){
			case 1:
				return this.renderVision();
				break;
			case 2:
				return this.renderGoal();
				break;
			default:
				return this.renderDefault();
		}
	}

	render(){
		return(
			<div className="InfoBoard col-6">
				{this.getBoard(this.state.display)}
			</div>);
	}

	renderVision(){
		return(<div>Vision</div>);
	}

	renderGoal(){
		return(<div>Goal</div>);
	}

	renderDefault(){
		return (<InfoModule stats="true" deadlines="true"/>);
	}
}

export default InfoBoard;
